mapper.insert:
replace into schoolwebsite.userInfo
    (idcardnumber, password,code,createTime)
    VALUE
    (#{teacher.idcardnumber.idCardNumber},#{teacher.idcardnumber.password},#{teacher.idcardnumber.code},NOW());

replace into schoolwebsite.teacher

    (teacherId
    ,IdCardNumber
    ,teacherName
    ,teacherSex
    ,branch

    ,teacherLevel

    ,createTime

    ,status


    ,description

    ,teacherSalary
    )

VALUE

    (#{teacher.id,jdbcType=VARCHAR}
    ,#{teacher.idcardnumber.idCardNumber}
    ,#{teacher.teacherName}
    ,#{teacher.sex}
    ,#{teacher.branch.id}

    ,#{teacher.level}

    ,now()

    ,#{teacher.status}


    ,#{teacher.description}

    ,#{teacher.salary}
    );

mapper.update:
update schoolwebsite.teacher set


    teacherName = #{teacher.teacherName},


    teacherSex = #{teacher.sex},



        branch = #{teacher.branch.id},



    teacherLevel = #{teacher.level},


    teacherSalary = #{teacher.salary},


    status = #{teacher.status},


    description = #{teacher.description}


where teacherId = #{teacher.id}

    and IdCardNumber = #{teacher.idcardnumber}

mapper.select:
select teacher.teacherId , userInfo.IdCardNumber , teacher.teacherName , teacher.teacherSex , teacher.branch , teacher.teacherLevel , teacher.teacherSalary , teacher.createTime , teacher.status , teacher.description , branch.branchId , branch.branchName from schoolwebsite.teacher, schoolwebsite.branch, schoolwebsite.userInfo where teacher.branch = branch.branchId and teacher.IdCardNumber = userInfo.IdCardNumber
and teacher.teacherName like CONCAT(#{teacherName},'%')
and teacher.branch = #{branch}

select teacher.teacherId , userInfo.IdCardNumber , teacher.teacherName , teacher.teacherSex , teacher.branch , teacher.teacherLevel , teacher.teacherSalary , teacher.createTime , teacher.status , teacher.description , branch.branchId , branch.branchName from schoolwebsite.teacher, schoolwebsite.branch, schoolwebsite.userInfo where teacher.branch = branch.branchId and teacher.IdCardNumber = userInfo.IdCardNumber
and userInfo.IdCardNumber like #{IdCardNUmber}
and teacher.teacherId = #{teacherId}
