replace into schoolwebsite.`teacher-class-course`

    ( id
    , teacherId
    , classId
    , courseId
    , createTime

        , status


        , description

    )

VALUES

    ( #{item.id}
    , #{item.teacher.id}
    , #{item.classes.id}
    , #{item.course.id}
    ,NOW()

        , #{item.status}


        , #{item.description}

    )

mapper.delete:
delete from schoolwebsite.`teacher-class-course` where
id = #{teacher_class.id}

and classId = #{teacher_class.classId}


and courseId = #{teacher_class.courseId}


and teacherId = #{teacher_class.teacherId}

mapper.update:
update schoolwebsite.`teacher-class-course` set


    teacherId = #{teacher_class.teacherId}


    ,classId = #{teacher_class.classId}


    ,courseId = #{teacher_class.courseId}


    ,status = #{teacher_class,status}


    ,description = #{teacher_class.description}


WHERE
id = #{teacher_class.id}

mapper.select:
select `teacher-class-course`.id , `teacher-class-course`.teacherId , `teacher-class-course`.classId , `teacher-class-course`.courseId , `teacher-class-course`.createTime , `teacher-class-course`.status , `teacher-class-course`.description , course.courseId , course.courseName , teacher.teacherId , teacher.IdCardNumber , teacher.teacherName , teacher.teacherSex , teacher.branch , branch.branchId , branch.branchName , class.classId , class.className from schoolwebsite.`teacher-class-course` , schoolwebsite.course , schoolwebsite.teacher , schoolwebsite.class , schoolwebsite.branch where `teacher-class-course`.teacherId = teacher.teacherId and `teacher-class-course`.courseId = course.courseId and `teacher-class-course`.classId = class.classId and teacher.branch = branch.branchId
and `teacher-class-course`.teacherId = #{teacherId}
and `teacher-class-course`.courseId = #{courseId}
and `teacher-class-course`.classId = #{classId}
